CREATE DEFINER=`root`@`localhost` FUNCTION `FN_CATEGORIA_PRODUCTOS`(_IDCATEGORIA INT) RETURNS int(11)
BEGIN
	SELECT
		SUM(DETA.CANTIDAD) INTO @CONTADOR
	FROM DETALLE_PRODUCTO DETA
	INNER JOIN PRODUCTO PRO 	ON DETA.IDPRODUCTO = PRO.IDPRODUCTO
	INNER JOIN CATEGORIA CAT 	ON PRO.IDCATEGORIA = CAT.IDCATEGORIA
	WHERE CAT.IDCATEGORIA = _IDCATEGORIA;
    
    IF( @CONTADOR IS NULL) THEN
		SET @CONTADOR = 0;
	END IF;
    
RETURN @CONTADOR;
END ;;
