CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_GET_DETALLE_PEDIDO`(IN _IDPEDIDO INT)
BEGIN
	SELECT
		DETA.IDPEDIDO, PED.TOTAL, PED.FECHA_PEDIDO,
        ESTADO.IDESTADO, ESTADO.DESCRIPCION ESTADO,
        MARCA.IDMARCA, MARCA.DESCRIPCION MARCA,
        CATEGORIA.IDCATEGORIA, CATEGORIA.DESCRIPCION CATEGORIA,
		DETA.IDPRODUCTO, PROD.DESCRIPCION PRODUCTO,
		DETA.IDCOLOR, COLOR.DESCRIPCION COLOR,
		DETA.CANTIDAD,
		DETA.PRECIO,
        DETA.IDDESCUENTO, DESCUENTO.PORCENTAJE,
		DETA.SUBTOTAL        
	FROM DETALLE_PEDIDO DETA
    INNER JOIN PEDIDO PED 		ON DETA.IDPEDIDO 	= PED.IDPEDIDO
		INNER JOIN ESTADO 		ON PED.IDESTADO 	= ESTADO.IDESTADO
	INNER JOIN PRODUCTO PROD 	ON DETA.IDPRODUCTO 	= PROD.IDPRODUCTO
		INNER JOIN MARCA 		ON PROD.IDMARCA 	= MARCA.IDMARCA
        INNER JOIN CATEGORIA 	ON PROD.IDCATEGORIA = CATEGORIA.IDCATEGORIA
	INNER JOIN COLOR 			ON DETA.IDCOLOR 	= COLOR.IDCOLOR
    LEFT JOIN DESCUENTO			ON DETA.IDDESCUENTO = DESCUENTO.IDDESCUENTO
	WHERE DETA.IDPEDIDO = _IDPEDIDO;
END ;;
