CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_GET_DETA_PRODUCTO_POR_CATEGORIA`(IN _IDCATEGORIA INT)
BEGIN
	SELECT
		DETA.IDPRODUCTO, PRO.DESCRIPCION PRODUCTO, 
		PRO.IDCATEGORIA, CAT.DESCRIPCION CATEGORIA,
		PRO.IDMARCA, MAR.DESCRIPCION MARCA,
        DETA.IDCOLOR, COLOR.DESCRIPCION COLOR,
		DETA.CANTIDAD, PRO.PRECIO, DETA.IDDESCUENTO, DES.DESCRIPCION DESCUENTO, DES.PORCENTAJE
	FROM PRODUCTO PRO
	LEFT JOIN DETALLE_PRODUCTO DETA ON PRO.IDPRODUCTO = DETA.IDPRODUCTO AND DETA.IDCOLOR = 
		(SELECT IDCOLOR
		FROM DETALLE_PRODUCTO
        WHERE CANTIDAD > 0 AND IDPRODUCTO = PRO.IDPRODUCTO
        ORDER BY CANTIDAD
        DESC LIMIT 1)
	LEFT JOIN CATEGORIA CAT USING (IDCATEGORIA)
	LEFT JOIN MARCA MAR 	USING (IDMARCA)
	LEFT JOIN COLOR USING(IDCOLOR)
	LEFT JOIN DESCUENTO DES USING(IDDESCUENTO)
	WHERE CAT.IDCATEGORIA = _IDCATEGORIA AND COLOR.IDCOLOR IS NOT NULL;
END ;;
