CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_READ_PEDIDOS_FILTROS`(IN _FECHA DATE, IN _IDESTADO INT, IN _CLIENTE VARCHAR(303))
BEGIN
	SELECT
		PEDIDO.IDPEDIDO, PEDIDO.TOTAL,  DATE_FORMAT(PEDIDO.FECHA_PEDIDO, '%d-%m-%Y') FECHA_PEDIDO,
		CLIENTE.IDCLIENTE, CLIENTE.NRO_DOCUMENTO,
        CONCAT(CLIENTE.APELLIDO_P, ' ', CLIENTE.APELLIDO_M, ' ', CLIENTE.NOMBRE) CLIENTE, 
		ESTADO.IDESTADO, ESTADO.DESCRIPCION ESTADO
	FROM PEDIDO
	INNER JOIN CLIENTE 	ON PEDIDO.IDCLIENTE = CLIENTE.IDCLIENTE
	INNER JOIN ESTADO 	ON PEDIDO.IDESTADO 	= ESTADO.IDESTADO
	WHERE
		CASE
			WHEN _FECHA IS NULL OR _FECHA = '' THEN 0
			ELSE
				CASE
					WHEN PEDIDO.FECHA_PEDIDO = _FECHA THEN 0
					ELSE 1
				END
		END = 0
		AND
		CASE
			WHEN _IDESTADO = 0 THEN 0
			ELSE
				CASE
					WHEN ESTADO.IDESTADO = _IDESTADO THEN 0
					ELSE 1
				END
		END = 0
		AND
		CASE
			WHEN _CLIENTE = '' THEN 0
			ELSE
				CASE
					WHEN
						CONCAT(CLIENTE.APELLIDO_P, ' ', CLIENTE.APELLIDO_M, ' ', CLIENTE.NOMBRE) LIKE CONCAT('%', _CLIENTE ,'%') 
						OR CLIENTE.NRO_DOCUMENTO LIKE CONCAT(_CLIENTE, '%')
						THEN 0
					ELSE 1
				END
		END = 0;
END ;;
DELIMITER ;
