			ELSE
				CASE
					WHEN ESTADO.IDESTADO = _IDESTADO THEN 0
					ELSE 1
				END
		END = 0
    ORDER BY PEDIDO.IDESTADO ASC, PEDIDO.FECHA_PEDIDO ASC;
